import styles from '../styles/pages/Home.module.css';
import { Navigationbar } from '../components/Navigationbar'
import { Mainarticle } from '../components/Mainarticle'
import { Articles } from '../components/Articles'
import { Footer } from '../components/Footer'
import api from '../service/api';
import { useState, useEffect } from "react";




export default function Main() {
  const [Page, SetPage] = useState(1);
  const [ArticleAll, SetArticleAll] = useState([]);
  const [ArticleInfo, SetArticleInfo] = useState([]);
  const [ArticleMain, SetArticleMain] = useState([]);

  useEffect(() => {
    api.get(`/articles?page=${Page}`).then(response => {
      const { total, limit, page, pages, docs } = response.data;
      SetArticleInfo({ total, limit, pages, page });
      SetArticleAll(docs);
      SetArticleMain(response.data.docs[1])

    })
  }, [Page]);

  if (!ArticleAll) {
    return <p>Carregando...</p>
  }
  console.log(Page)

  const prevPage = () => {
    if (parseInt(ArticleInfo.page) === 1) return
    SetPage(parseInt(ArticleInfo.page) - 1);
  }

  const nextPage = () => {
    if (ArticleInfo.page === ArticleInfo.pages) return
    SetPage(parseInt(ArticleInfo.page) + 1);
  }




  return (
    <div className={styles.container}>


      <header className={styles.header}>
        <Navigationbar />
      </header>

      <section className={styles.section}>
        <Mainarticle ArticleM={ArticleMain} />
        <div >
          <button disabled={parseInt(ArticleInfo.page) === 1} onClick={prevPage}>Anterior</button>
          <span>{ArticleInfo.page}</span>
          <button disabled={parseInt(ArticleInfo.page) === parseInt(ArticleInfo.pages)} onClick={nextPage}>Proximo</button>
        </div>


        <div className={styles.containerArticle}>

          {ArticleAll.map((item, index) => {
            if (index > 0) {
              return (
                <Articles ArticleM={item} />
              )
            }
          })
          }
        </div>


      </section>

      <footer className={styles.footer}>
        <Footer />
      </footer>
    </div>
  )
}
