import styles from '../styles/components/suggestion.module.css';

export function Suggestion() {
  const Sendemail = () => {
    let content = document.getElementById('send').value;
    var xmlhttp = new XMLHttpRequest();
    xmlhttp.open("GET", 'http://meuip.com/api/meuip.php');
    xmlhttp.send();
    xmlhttp.onload = function (e) {
      alert("Seu IP é: " + xmlhttp.response);
    }
    alert('Muito Obrigado!! a sua sugestão é muito importante para nós...');

  }
  return (
    <div className={styles.container}>
      <div className={styles.suggestionleft}>
        <h3>A sua sugestão é muito importante para nós #vamosprogramarjuntos</h3>
      </div>
      <div className={styles.suggestion}>
        <input id='send' type="text" placeholder=" Diga para nós a sua sugestão de conteudo..." />
        <button onClick={Sendemail}><i class="fas fa-paper-plane"></i></button>
      </div>
    </div>
  )
}
